import React from 'react';
import "./Question.css";
export default function Question(props) {

    let [counter, setCounter] = useState(0);

    function changeDisplay() {
        let increase = counter + 1;
        setCounter(increase);
        let message = " ";
        if (increase == 2) {
            message = "answer: " + props.answer;
        } else if (increase == 1) {
            message = "question: " + props.question
        } else {
            message = "pointValue: " + props.point
        }
    }

    return (
        <div onClick={changeDisplay} class="boxes">
            <p>{message}</p>
        </div>
    );

  }
